jQuery(document).ready(function () {
	jQuery(".gallery-icon a").each(function () {
		var b = jQuery(this).children().attr("src");
		var e = b.lastIndexOf("-");
		var a = b.lastIndexOf(".");
		var d = b.substring(e, a);
		var c = b.replace(d, "");
		jQuery(this).attr("rel", "cyberchimps-lightbox");
		jQuery(this).attr("href", c)
	})
});